{
    "txData/server/settings.cfg": {
        "parser": "file",
        "find": {
            "set sv_forceIndirectListing": "set sv_forceIndirectListing {{server.build.env.SETTINGS_PROXY}}",
            "set sv_listingHostOverride": "set sv_listingHostOverride \"{{server.build.env.SETTINGS_PROXYURL}}\"",
            "set sv_proxyIPRanges": "set sv_proxyIPRanges \"{{server.build.env.SETTINGS_PROXYIPRANGES}}/32\"",
            "set sv_endpoints": "set sv_endpoints \"{{server.build.env.SETTINGS_PROXYURL}}:{{server.build.default.port}}\"",
            "endpoint_add_tcp": "endpoint_add_tcp \"0.0.0.0:{{server.build.default.port}}\"",
            "endpoint_add_udp": "endpoint_add_udp \"0.0.0.0:{{server.build.default.port}}\"",
            "sv_hostname": "sv_hostname \"{{server.build.env.SERVER_HOSTNAME}}\"",
            "sets sv_projectName": "sets sv_projectName \" {{server.build.env.SERVER_PROJECTNAME}}\"",
            "sets sv_projectDesc": "sets sv_projectDesc \"{{server.build.env.SERVER_DESCRIPTION}}\"",
            "sets tags": "sets tags \"{{server.build.env.SERVER_TAGES}}\"",
            "set sv_licenseKey": "set sv_licenseKey {{server.build.env.FIVEM_LICENSE}}",
            "set steam_webApiKey": "set steam_webApiKey {{server.build.env.STEAM_WEBAPIKEY}}",
            "sv_maxclients": "sv_maxclients {{server.build.env.MAX_PLAYERS}}",
            "sv_enforceGameBuild": "sv_enforceGameBuild {{server.build.env.BUILD}}",
            "sets locale": "sets locale \"{{server.build.env.SERVER_LOCALE}}\"",
            "sv_pureLevel": "sv_pureLevel {{server.build.env.SERVER_PURELEVEL}}"
        }
    },
    "txData/server/server.cfg": {
        "parser": "file",
        "find": {
            "header": "# ==================================================\n#   Diese Datei wird automatisch verwaltet!\n#   Manuelle \u00c4nderungen k\u00f6nnen \u00fcberschrieben werden.\n# ==================================================\n",
            "exec_settings": "exec settings.cfg",
            "exec_resource": "exec resource.cfg",
            "exec_permission": "exec permission.cfg",
            "exec_easyadmin": "exec easyadmin.cfg"
        }
    }
}
